import {createContext,useReduser} from "react";

const INITIAL_STATE={
    user:null,
    isFetching:false,
    error:false
};

export const Context=createContext();

export const ContextProvider=({children})=>{
    const [state,dispatch]=useReduser(Reduser,INITIAL_STATE);
    
    return (
       <Context.Provider 
             value={{
                 user:state.user,
                 isFetching:state.isFetching,
                 error:state.error,
                 dispatch,
             }}
             >{children}</Context.Provider>

    );
}
